import React, { useState, useRef, useEffect } from "react";
import Controls from "./Controls";
import Details from "./Details";

function Player(props) {
  // create audio element
  const audioEl = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    if (isPlaying) {
      // jodi "isPlaying" true hoy tobe audio play hobe
      audioEl.current.play();
    } else {
      // nahola audio pause hoya jabe
      audioEl.current.pause();
    }
  });

  // create skip function
  const SkipSong = (forwards = true) => {
    if (forwards) {
      props.setCurrentSongIndex(() => {
        let temp = props.currentSongIndex;
        temp++;

        if (temp > props.songs.length - 1) {
          temp = 0;
        }

        return temp;
      });
    } else {
      props.setCurrentSongIndex(() => {
        let temp = props.currentSongIndex;
        temp--;

        if (temp < 0) {
          temp = props.songs.length - 1;
        }

        return temp;
      });
    }
  };

  return (
    <>
      <div>
        <h1>Plese select ..</h1>
        <hr />

        <div className="menu_tab">
          <div className="menuu">
            <button className="btn">ALL</button>
            <button className="btn">DJ</button>
            <button className="btn">MJ</button>
            <button className="btn">BJ</button>
          </div>
        </div>
      </div>

      <div className="bhimm">
        <div className="row">
          <div className="c-player">
            <h4>Playing now</h4>
            <Details song={props.songs[props.currentSongIndex]} />
            <Controls
              isPlaying={isPlaying}
              setIsPlaying={setIsPlaying}
              SkipSong={SkipSong}
            />
            <audio
              className="c-player--audio"
              src={props.songs[props.currentSongIndex].src}
              ref={audioEl}
              controls
            ></audio>
            <p>
              Next up:{" "}
              <span>
                {props.songs[props.nextSongIndex].title} by{" "}
                {props.songs[props.nextSongIndex].artist}
              </span>
            </p>
          </div>
        </div>
      </div>
    </>
  );
}

export default Player;
